              style={{ 
                pointerEvents: 'auto',
                zIndex: 10,
                position: 'relative'
              }}
            >
              <UserPlus className="h-4 w-4" />
              Add Worker
            </Button>
          </div>

          {/* Manage Workers Button with Dialog */}
          <div className="relative">
            <Button 
