              </Button>
            </DialogFooter>
          </form>
        </DialogContent>
      </Dialog>

      {/* Manage Workers Dialog */}
      <Dialog open={isManageWorkersOpen} onOpenChange={setIsManageWorkersOpen}>
        <DialogContent className="sm:max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
