      const newWorker: Worker = {
        id: `worker-${Date.now()}`,
        name: workerForm.name.trim(),
        employeeId: workerForm.employeeId.trim(),
        department: workerForm.department.trim() || '',
        position: workerForm.position.trim() || '',
        isPacker: workerForm.isPacker,
