      setWorkers(workersData);
      setAttendanceRecords(attendanceData);

      if (onAttendanceUpdate) {
        onAttendanceUpdate(attendanceData);
      }
    } catch (error) {
      console.error('Error loading data:', error);
      toast.error('Failed to load data');
    } finally {
      setLoading(false);
    }
