                    />
                  </div>
                  <div>
                    <Label htmlFor="position">Position</Label>
                    <Input
                      id="position"
                      value={workerForm.position}
                      onChange={(e) => setWorkerForm(prev => ({ ...prev, position: e.target.value }))}
                      placeholder="Enter position"
                    />
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="isPacker"
