    try {
      const success = await deleteWorker(workerId);
      if (success) {
        setWorkers(prev => prev.filter(w => w.id !== workerId));
        setAttendanceRecords(prev => prev.filter(r => r.workerId !== workerId));
        toast.success('Worker deleted successfully');
