    try {
      const worker = workers.find(w => w.id === workerId);
      if (!worker) return;

      const existingRecord = getWorkerAttendance(workerId, selectedDate);

      const attendanceRecord: AttendanceRecord = {
        id: existingRecord?.id || `attendance-${workerId}-${selectedDate}`,
