    const dateAttendance = getAttendanceForDate(selectedDate);
    const csvContent = [
      ['Employee ID', 'Name', 'Department', 'Position', 'Status', 'Overtime', 'Notes'].join(','),
      ...dateAttendance.map(record => {
        const worker = workers.find(w => w.id === record.workerId);
        return [
          worker?.employeeId || '',
          record.workerName,
          worker?.department || '',
          worker?.position || '',
          record.status,
          record.overtime || 'no',
          record.notes || ''
        ].join(',');
      })
