                />
              </div>
              <div>
                <Label htmlFor="employeeId">Employee ID *</Label>
                <Input
                  id="employeeId"
                  value={workerForm.employeeId}
                  onChange={(e) => setWorkerForm(prev => ({ ...prev, employeeId: e.target.value }))}
                  placeholder="Enter employee ID"
