        const worker = workers.find(w => w.id === record.workerId);
        return [
          worker?.employeeId || '',
          record.workerName,
          worker?.department || '',
          worker?.position || '',
          record.status,
          record.overtime || 'no',
          record.notes || ''
        ].join(',');
      })
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `attendance-${selectedDate}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
    toast.success('Attendance exported successfully');
  };

  const dateAttendance = getAttendanceForDate(selectedDate);
  const presentCount = dateAttendance.filter(r => r.status === AttendanceStatus.PRESENT).length;
  const absentCount = dateAttendance.filter(r => r.status === AttendanceStatus.ABSENT).length;
  const halfDayCount = dateAttendance.filter(r => r.status === AttendanceStatus.HALF_DAY).length;
  const packers = workers.filter(w => w.isPacker);

  // Handle button clicks with explicit logging
  const handleAddWorkerClick = () => {
    console.log('Add Worker button clicked - opening dialog');
    setIsAddWorkerOpen(true);
  };

  const handleManageWorkersClick = () => {
    console.log('Manage Workers button clicked - opening dialog');
    setIsManageWorkersOpen(true);
  };

  // Main Attendance View
  return (
    <div className="space-y-6 p-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Attendance Management</h2>
          <p className="text-sm text-gray-600">Track daily worker attendance</p>
        </div>
        <div className="flex items-center gap-4">
          {/* Add Worker Button with Dialog */}
          <div className="relative">
            <Button 
              onClick={handleAddWorkerClick}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md flex items-center gap-2 cursor-pointer"
              style={{ 
                pointerEvents: 'auto',
                zIndex: 10,
                position: 'relative'
              }}
            >
              <UserPlus className="h-4 w-4" />
              Add Worker
            </Button>
          </div>

          {/* Manage Workers Button with Dialog */}
          <div className="relative">
            <Button 
              onClick={handleManageWorkersClick}
              variant="outline"
              className="border-gray-300 hover:bg-gray-50 px-4 py-2 rounded-md flex items-center gap-2 cursor-pointer"
              style={{ 
                pointerEvents: 'auto',
                zIndex: 10,
                position: 'relative'
              }}
            >
              <Settings className="h-4 w-4" />
              Manage Workers
            </Button>
          </div>

          <div className="flex items-center gap-2">
            <Users className="h-5 w-5 text-gray-500" />
            <span className="text-sm font-medium">{workers.length} Workers</span>
