  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Worker, AttendanceRecord } from '@/types';
import {
  getWorkers,
  addWorker,
  deleteWorker,
  getTodayAttendance,
  updateAttendanceStatus,
  updateOvertimeStatus,
  updatePackerStatus,
} from '@/lib/attendance-utils';

const AttendanceManagement: React.FC = () => {
  const [workers, setWorkers] = useState<Worker[]>([]);
  const [attendance, setAttendance] = useState<AttendanceRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [newWorkerName, setNewWorkerName] = useState('');
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isManageDialogOpen, setIsManageDialogOpen] = useState(false);
  const [deleteWorkerId, setDeleteWorkerId] = useState<string | null>(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [workersData, attendanceData] = await Promise.all([
        getWorkers(),
        getTodayAttendance(),
      ]);
      setWorkers(workersData);
      setAttendance(attendanceData);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddWorker = async () => {
    if (!newWorkerName.trim()) return;

    try {
      await addWorker(newWorkerName.trim());
      setNewWorkerName('');
      setIsAddDialogOpen(false);
      await loadData();
    } catch (error) {
      console.error('Error adding worker:', error);
    }
  };

  const handleDeleteWorker = async (workerId: string) => {
    try {
      await deleteWorker(workerId);
      setDeleteWorkerId(null);
      await loadData();
    } catch (error) {
      console.error('Error deleting worker:', error);
    }
  };

  const handleAttendanceToggle = async (workerId: string, status: 'present' | 'absent' | 'half_day') => {
    try {
      await updateAttendanceStatus(workerId, status);
      await loadData();
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  const handleOvertimeToggle = async (workerId: string, isOvertime: boolean) => {
    try {
      await updateOvertimeStatus(workerId, isOvertime);
      await loadData();
    } catch (error) {
      console.error('Error updating overtime:', error);
    }
  };

  const handlePackerToggle = async (workerId: string, isPacker: boolean) => {
    try {
      await updatePackerStatus(workerId, isPacker);
      await loadData();
    } catch (error) {
      console.error('Error updating packer status:', error);
    }
  };

  const getAttendanceRecord = (workerId: string): AttendanceRecord | null => {
    return attendance.find(record => record.worker_id === workerId) || null;
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case 'present': return 'default';
      case 'absent': return 'destructive';
      case 'half_day': return 'secondary';
      default: return 'outline';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg">Loading attendance data...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-4">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold">Attendance Management</h2>
        <div className="flex gap-2">
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button 
                variant="outline" 
                size="sm"
                className="flex items-center gap-2 hover:bg-gray-50 transition-colors duration-200"
                style={{ zIndex: 1 }}
              >
                <UserPlus className="h-4 w-4" />
                Add Worker
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Add New Worker</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="workerName">Worker Name</Label>
                  <Input
                    id="workerName"
                    value={newWorkerName}
                    onChange={(e) => setNewWorkerName(e.target.value)}
                    placeholder="Enter worker name"
                    onKeyPress={(e) => e.key === 'Enter' && handleAddWorker()}
                  />
                </div>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setIsAddDialogOpen(false)}>
                  Cancel
                </Button>
                <Button onClick={handleAddWorker} disabled={!newWorkerName.trim()}>
                  Add Worker
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>

          <Dialog open={isManageDialogOpen} onOpenChange={setIsManageDialogOpen}>
            <DialogTrigger asChild>
              <Button 
                variant="default" 
                size="sm"
                className="flex items-center gap-2 hover:bg-blue-600 transition-colors duration-200"
                style={{ zIndex: 1 }}
              >
                <Users className="h-4 w-4" />
                Manage Workers
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Manage Workers</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                {workers.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    No workers found. Add some workers to get started.
                  </div>
                ) : (
                  <div className="grid gap-4">
                    {workers.map((worker) => {
                      const attendanceRecord = getAttendanceRecord(worker.id);
                      const status = attendanceRecord?.status || 'present';
                      const isOvertime = attendanceRecord?.is_overtime || false;
                      const isPacker = worker.is_packer || false;

                      return (
                        <Card key={worker.id} className="p-4">
                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-4">
                              <div>
                                <h3 className="font-medium">{worker.name}</h3>
                                <p className="text-sm text-gray-500">ID: {worker.id}</p>
                              </div>
                              <Badge variant={getStatusBadgeVariant(status)}>
                                {status.replace('_', ' ').toUpperCase()}
                              </Badge>
                              {isOvertime && (
                                <Badge variant="outline" className="bg-yellow-50">
                                  OVERTIME
                                </Badge>
                              )}
                              {isPacker && (
                                <Badge variant="outline" className="bg-green-50">
                                  PACKER
                                </Badge>
                              )}
                            </div>
                            <div className="flex items-center gap-4">
                              <div className="flex items-center gap-2">
                                <Label htmlFor={`attendance-${worker.id}`} className="text-sm">
                                  Attendance:
                                </Label>
                                <select
                                  id={`attendance-${worker.id}`}
                                  value={status}
                                  onChange={(e) => handleAttendanceToggle(worker.id, e.target.value as 'present' | 'absent' | 'half_day')}
                                  className="border rounded px-2 py-1 text-sm"
                                >
                                  <option value="present">Present</option>
                                  <option value="absent">Absent</option>
                                  <option value="half_day">Half Day</option>
                                </select>
                              </div>
                              <div className="flex items-center gap-2">
                                <Label htmlFor={`overtime-${worker.id}`} className="text-sm">
                                  Overtime:
                                </Label>
                                <Switch
                                  id={`overtime-${worker.id}`}
                                  checked={isOvertime}
                                  onCheckedChange={(checked) => handleOvertimeToggle(worker.id, checked)}
                                />
                              </div>
                              <div className="flex items-center gap-2">
                                <Label htmlFor={`packer-${worker.id}`} className="text-sm">
                                  Packer:
                                </Label>
                                <Switch
                                  id={`packer-${worker.id}`}
                                  checked={isPacker}
                                  onCheckedChange={(checked) => handlePackerToggle(worker.id, checked)}
                                />
                              </div>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => setDeleteWorkerId(worker.id)}
                                className="text-red-600 hover:text-red-700 hover:bg-red-50"
                              >
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </div>
                          </div>
                        </Card>
                      );
                    })}
                  </div>
                )}
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Today's Attendance Summary</CardTitle>
        </CardHeader>
        <CardContent>
          {workers.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              No workers found. Add some workers to get started.
            </div>
          ) : (
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {workers.map((worker) => {
                const attendanceRecord = getAttendanceRecord(worker.id);
                const status = attendanceRecord?.status || 'present';
                const isOvertime = attendanceRecord?.is_overtime || false;
                const isPacker = worker.is_packer || false;

                return (
                  <Card key={worker.id} className="p-4">
                    <div className="flex items-center justify-between mb-3">
                      <h3 className="font-medium">{worker.name}</h3>
                      <div className="flex gap-1">
                        <Badge variant={getStatusBadgeVariant(status)}>
                          {status.replace('_', ' ').toUpperCase()}
                        </Badge>
                        {isOvertime && (
                          <Badge variant="outline" className="bg-yellow-50">
                            OT
                          </Badge>
                        )}
                        {isPacker && (
                          <Badge variant="outline" className="bg-green-50">
                            P
                          </Badge>
                        )}
                      </div>
                    </div>
                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <Label htmlFor={`quick-attendance-${worker.id}`} className="text-sm">
                          Status:
                        </Label>
                        <select
                          id={`quick-attendance-${worker.id}`}
                          value={status}
                          onChange={(e) => handleAttendanceToggle(worker.id, e.target.value as 'present' | 'absent' | 'half_day')}
                          className="border rounded px-2 py-1 text-xs"
                        >
                          <option value="present">Present</option>
                          <option value="absent">Absent</option>
                          <option value="half_day">Half Day</option>
                        </select>
                      </div>
                      <div className="flex items-center justify-between">
                        <Label htmlFor={`quick-overtime-${worker.id}`} className="text-sm">
                          Overtime:
                        </Label>
                        <Switch
                          id={`quick-overtime-${worker.id}`}
                          checked={isOvertime}
                          onCheckedChange={(checked) => handleOvertimeToggle(worker.id, checked)}
                          size="sm"
                        />
                      </div>
                    </div>
                  </Card>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      <AlertDialog open={!!deleteWorkerId} onOpenChange={() => setDeleteWorkerId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Worker</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete this worker? This action will also remove all associated attendance records and cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => deleteWorkerId && handleDeleteWorker(deleteWorkerId)}
              className="bg-red-600 hover:bg-red-700"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
};

